mapper.insert:
insert into NOTICE
	( NOT_NO
    , NOT_TITLE
	, NOT_CONTENT
	, NOT_DATE)
values
	( ( select *             
		from ( select ifnull(max(cast(NOT_NO as unsigned)), 0) + 1
			   from NOTICE ) A )
    , #{NOT_TITLE}
    , #{NOT_CONTENT}
    , date_format(sysdate(), '%Y%m%d%H%i%s') );
