mapper.insert:
		insert into NOTICE
			( NOT_NO
		    , NOT_TITLE
			, NOT_CONTENT
			, NOT_FILE
			, NOT_DATE)
		values
			( concat('NO',(
			  select *
			  from (select ifnull(max(cast(trim(leading 'NO' from NOT_NO) as unsigned)), 0) + 1
      			    from NOTICE ) A ))
		    , #{NOT_TITLE}
		    , #{NOT_CONTENT}
		    , #{NOT_FILE}
		    , date_format(sysdate(), '%Y%m%d%H%i%s') );
