mapper.insert:
insert into NOTICE
	( NOT_NO
    , NOT_TITLE
	, NOT_CONTENT
	, NOT_DATE)
values
	( ( select *             
		from ( select ifnull(max(cast(NOT_NO as unsigned)), 0) + 1
			   from NOTICE ) A )
    , #{NOT_TITLE}
    , #{NOT_CONTENT}
    , date_format(sysdate(), '%Y%m%d%H%i%s') );

mapper.select:
select NOT_NO, NOT_TITLE, NOT_CONTENT, date_format(ifnull(NOT_REDATE, NOT_DATE), '%Y-%m-%d') as "DATE"
from NOTICE
order by NOT_NO desc;

select STORE_NO, STORE_NAME, STORE_CATEGORY, STORE_TEL, STORE_LOCATION
from STORE
order by STORE_NO desc;

select SREP_NO, CUS_NO, STORE_NO, STORE_NAME, SREP_CONTENT, date_format(SREP_DATE, '%Y-%m-%d') as "DATE", SREP_PROCESS
from STORE_REPORT
order by SREP_NO desc;

select CREP_NO, OWN_NO, CREP_CONTENT, CREP_CONTENT, date_format(CREP_DATE, '%Y-%m-%d') as "DATE", CREP_PROCESS
from REVIEW_REPORT
order by CREP_NO desc;
