mapper.insert:
insert into CUSTOMER
	( CUS_NO    ,
	  CUS_ID    ,
	  CUS_NAME  ,
	  CUS_BIRTH ,
	  CUS_TEL   ,
	  CUS_EMAIL ,
	  CUS_GENDER,
	  CUS_NICK  ,
	  CUS_DATE)  
values
	(CONCAT('CU', ( select *             
		from (select ifnull(max(cast(trim(leading 'CU' from CUS_NO) as unsigned)), 0) + 1
			  from CUSTOMER ) A ))
	, #{CUS_ID}
	, #{CUS_NAME}
	, #{CUS_BIRTH}
	, #{CUS_TEL}
	, #{CUS_EMAIL}
	, #{CUS_GENDER}
	, CONCAT('CU', ( select *             
		from (select ifnull(max(cast(trim(leading 'CU' from CUS_NO) as unsigned)), 0) + 1
			  from CUSTOMER ) A ))
	, DATE_FORMAT(SYSDATE(), '%Y%m%d%H%i%s'));
