mapper.select:
select *
  from LIKE_LIST
 WHERE CUS_NO = #{CUS_NO}
   AND STORE_NO = #{STORE_NO};

mapper.insert:
insert into LIKE_LIST
values( concat("LL" ,ifnull((select cast(max(replace(LIKE_NO, "LL", "")) as unsigned)+1 from LIKE_LIST a), 1))
      , #{CUS_NO}
      , #{STORE_NO});

mapper.delete:
delete from LIKE_LIST
 where STORE_NO = #{STORE_NO}
   and CUS_NO = #{CUS_NO};

mapper.select:
SELECT RES_NO, RES_TIME
  FROM RESERVATION
 WHERE STORE_NO = #{STORE_NO}
   AND SEAT_NO = #{SEAT_NO}
   AND RES_DATE = date_format(#{DATE}, '%Y%m%d');
