mapper.insert:
 		insert into OWNER
			( OWN_NO    ,
			  OWN_ID    ,
			  OWN_PW  ,
			  OWN_NAME ,
			  OWN_TEL   ,
			  OWN_EMAIL ,
			  OWN_CRN, 
			  STORE_NO  ,
			  OWN_DATE)  
		values
			(CONCAT('OW', ( select *             
				from (select ifnull(max(cast(trim(leading 'OW' from OWN_NO) as unsigned)), 0) + 1
					  from OWNER ) A ))
			, #{OWN_ID}
			, #{OWN_PW}
			, #{OWN_NAME}
			, #{OWN_TEL}
			, #{OWN_EMAIL}
			, #{OWN_CRN}
			, CONCAT('ST', ( select *             
				from (select ifnull(max(cast(trim(leading 'ST' from STORE_NO) as unsigned)), 0) + 1
					  from OWNER ) A ))
			, DATE_FORMAT(SYSDATE(), '%Y%m%d%H%i%s'));
