mapper.select:
		select 
 			
 		from  OWNER
		
			AND OWN_ID = #{OWN_ID}
			AND OWN_PW = #{OWN_PW}
			AND OWN_CRN = #{OWN_CRN} 
			AND OWN_EMAIL = #{OWN_EMAIL}

mapper.insert:
 		insert into OWNER
			( OWN_NO    
			 , OWN_ID    
			 , OWN_PW  
			 , OWN_NAME 
			 , OWN_TEL   
			 , OWN_EMAIL 
			 , OWN_CRN 
			 , STORE_NO  
			 , OWN_DATE)  
		values
			(CONCAT('OW', ( select *             
				from (select ifnull(max(cast(trim(leading 'OW' from OWN_NO) as unsigned)), 0) + 1
					  from OWNER ) A ))
			, #{OWN_ID}
			, #{OWN_PW}
			, #{OWN_NAME}
			, #{OWN_TEL}
			, #{OWN_EMAIL}
			, #{OWN_CRN}
			, '0'
			, DATE_FORMAT(SYSDATE(), '%Y%m%d%H%i%s'));

mapper.update:
update OWNER
set OWN_PW = #{OWN_PW}
where OWN_ID = #{OWN_ID};
