mapper.insert:
 		insert into OWNER
			( OWN_NO    ,
			  OWN_ID    ,
			  OWN_PW  ,
			  OWN_NAME ,
			  OWN_TEL   ,
			  OWN_EMAIL ,
			  OWN_CRN, 
			  STORE_NO  ,
			  OWN_DATE)  
		values
			( CONCAT('OWN_', (select * 
							  from (select ifnull(MAX(cast(trim(leading 'OW_' from #{OWN_NO})as unsigned)), 0) + 1
		 		              from OWNER) A))
			, #{OWN_ID}
			, #{OWN_PW}
			, #{OWN_NAME}
			, #{OWN_TEL}
			, #{OWN_EMAIL}
			, #{OWN_CRN}
			, #{STORE_NO}
			, DATE_FORMAT(SYSDATE(), '%Y%m%d%H%i%s'));

mapper.select:
 		select *
 		from  OWNER
		where OWN_ID=#{OWN_ID}
		and   OWN_PW=#{OWN_PW};
