mapper.insert:
INSERT INTO PAYMENT
	( PAY_NO
	, RES_NO
	, CUS_NO
	, MERCHANT_UID
	, APPLY_NUM
	, BUYER_NAME
	, BUYER_EMAIL
	, BUYER_TEL
	, CARD_NAME
	, CARD_NUMBER
	, NAME
	, PAID_AMOUNT
	, PAID_AT
	)
VALUES
	( concat("PA", IFNULL((select MAX(cast(replace(PAY_NO, "PA", "") AS unsigned))+1 FROM PAYMENT A), 1))
	, concat("RS", IFNULL((select MAX(cast(replace(RES_NO, "RS", "") AS unsigned))+1 FROM RESERVATION R), 1))
	, #{CUS_NO}
	, #{merchant_uid}
	, #{apply_num}
	, #{buyer_name}
	, #{buyer_email}
	, #{buyer_tel}
	, #{card_name}
	, #{card_number}
	, #{name}
	, #{paid_amount}
	, date_format(from_unixtime(#{PAID_AT}), '%Y%m%d%H%i%s')
	);
	
INSERT INTO RESERVATION
	( RES_NO
	, CUS_NO
	, STORE_NO
	, PAY_NO
	, SEAT_NO
	, CUS_NAME
	, CUS_TEL
	, RES_REQ
	, RES_PEOPLE
	, RES_DATE
	, RES_TIME
	, REQ_STATE
	, RES_PAYTIME
	)
VALUES
	( concat("RS" ,ifnull((select max(cast(replace(RES_NO, "RS", "") as unsigned))+1 from RESERVATION a), 1))
	, #{CUS_NO}
	, #{STORE_NO}
	, concat("PA" ,ifnull((select max(cast(replace(PAY_NO, "PA", "") as unsigned)) from PAYMENT a), 1))
	, #{SEAT_NO}
	, #{CUS_NAME}
	, #{CUS_TEL}
	, #{RES_REQ} 
	, #{RES_PEOPLE}
	, DATE_FORMAT(#{RES_DATE}, '%Y%m%d')
	, concat(SUBSTRING_INDEX(#{RES_TIME}, ':', 1), SUBSTRING_INDEX(#{RES_TIME}, ':', -1))
	, '0'
	, date_format(now(), '%Y%m%d%H%i%s')
	);

		INSERT INTO RESERVATION_MENU
			( RES_NO
			, PAY_NO
			, MENU_NO
			, STORE_NO
			, MENU_COUNT
			, PAID_PRICE
			)
		VALUES
	
			(
				  concat("RS" ,ifnull((select max(cast(replace(RES_NO, "RS", "") as unsigned)) from RESERVATION r), 1))
 				, concat("PA" ,ifnull((select max(cast(replace(PAY_NO, "PA", "") as unsigned)) from PAYMENT a), 1))
				, #{menuDTO.MENU_NO}
				, #{menuDTO.STORE_NO}
				, #{menuDTO.MENU_COUNT}
				, (SELECT MENU_PRICE FROM MENU WHERE STORE_NO=#{menuDTO.STORE_NO} AND MENU_NO=#{menuDTO.MENU_NO}) * #{menuDTO.MENU_COUNT}
			)

mapper.select:
SELECT RES_NO
  FROM RESERVATION
 WHERE CUS_NO = #{CUS_NO} 
   AND RES_PAYTIME = (select MAX(RES_PAYTIME) FROM RESERVATION A WHERE CUS_NO = #{CUS_NO});
