mapper.insert:
insert		
	into STORE_IMAGE
		( STORE_NO
		, PHOTO_NO
		, PHOTO_NAME
		, PHOTO_TYPE
		)
	values
		( #{STORE_NO}
		, (select concat('PH', ifnull(max(cast(REPLACE(#{MENU_NO}, 'ME', '') as unsigned)), 1))
			 from STORE_IMAGE a)
		, #{PHOTO_NAME}
		, #{MENU_NO}
		)
		;
insert		
	into MENU
		( STORE_NO
		, MENU_NO
		, MENU_NAME
		, MENU_PRICE
		, MENU_HIDE
		, MENU_INFO
		)
	values
		( #{STORE_NO}										
		, #{MENU_NO}
		, #{MENU_NAME}
		, #{MENU_PRICE}
		, #{MENU_HIDE}
		, #{MENU_INFO}
		)
		;

mapper.update:
	update MENU m, STORE_IMAGE s
   	   set 
		  	m.MENU_NAME = #{MENU_NAME}
	  	  , s.PHOTO_NAME = #{PHOTO_NAME}
	  	  , m.MENU_INFO = #{MENU_INFO}
	  	  , m.MENU_PRICE = #{MENU_PRICE}
      	  , m.MENU_HIDE = #{MENU_HIDE}
   	 where 1 = 1
   	   and m.MENU_NO = s.PHOTO_TYPE
   	   and m.STORE_NO = #{STORE_NO}
       and m.MENU_NO = #{MENU_NO}
       ;

mapper.select:
 		select m.MENU_NAME, s.PHOTO_NAME, m.MENU_INFO, m.MENU_PRICE, m.MENU_HIDE, m.MENU_NO
		  from MENU m 
		  join STORE_IMAGE s
		    on m.MENU_NO = s.PHOTO_TYPE
		 where 1 = 1
	 	   and m.MENU_NO = #{MENU_NO}
	   	   and m.STORE_NO = #{STORE_NO}
	   	   ;

 		SELECT CONCAT("ME", ifnull(MAX(CAST(REPLACE(MENU_NO, "ME", "") AS UNSIGNED)), 0) + 1) as "MENU_NO"
		  FROM MENU
		 where 1 = 1
		   and STORE_NO = #{STORE_NO}
		   ;

mapper.delete:
 		DELETE A, B
		  FROM MENU A INNER JOIN STORE_IMAGE B 
		 WHERE 1 = 1 
		   AND A.MENU_NO = B.PHOTO_TYPE 
		   AND A.STORE_NO = B.STORE_NO
		   AND A.MENU_NO in
		
			#{MENUList}
		
		   AND A.STORE_NO in 
		
			#{STOREPK}
		   
		   ;

mapper.select:
SELECT STORE_NO, OWN_NO
  FROM OWNER
 WHERE 1 = 1
   and OWN_NO = #{OWN_NO}
     ;

select R.REV_NO , R.CUS_NICK , R.STORE_NO , (SELECT GROUP_CONCAT(concat(MENU_NAME,'/',MENU_COUNT)) MENU_NAME FROM RESERVATION_MENU WHERE RES_NO = R.RES_NO GROUP BY RES_NO) MENU_NAME , (select SEAT_NAME from SEAT where SEAT_NO = R.SEAT_NO and STORE_NO = R.STORE_NO) SEAT_NAME , R.REV_CONTENT , CAST(str_to_date(R.REV_DATE, '%Y%m%d%H%i%s') AS CHAR(30)) REV_DATE , R.REV_SCORE , R.REV_GOOD , ifnull(R.REV_IMAGE, '0') REV_IMAGE , RA.ANSWER_CONTENT , CAST(ifnull(concat('(수정) ', str_to_date(RA.ANSWER_UPDATE_DATE, '%Y%m%d%H%i%s')), str_to_date(RA.ANSWER_DATE, '%Y%m%d%H%i%s')) AS CHAR(30)) ANSWER_DATE , ifnull(RA.ANSWER_CONTENT, '2') 'SELECT' from REVIEW R left join REVIEW_ANSWER RA on R.REV_NO = RA.REV_NO where 1=1 and R.STORE_NO = #{STORE_NO}
and RA.ANSWER_CONTENT is null
and RA.ANSWER_CONTENT is not null
order by REV_DATE desc ;

mapper.update:
 		update REVIEW_ANSWER
		set ANSWER_CONTENT = #{ANSWER_CONTENT}, ANSWER_UPDATE_DATE = date_format(now(),'%Y%m%d%H%i%s')
		where REV_NO = #{REV_NO};

mapper.insert:
 		insert into REVIEW_REPORT (RREP_NO, REV_NO, STORE_NO, RREP_CONTENT, RREP_DATE, RREP_PROCESS)
		VALUES
		( concat("RR", IFNULL((select MAX(cast(replace(RREP_NO, "RR", "") AS unsigned))+1 FROM REVIEW_REPORT A), 1))
		, #{REV_NO}
		, #{STORE_NO}
		, if(#{RREP_CONTENT} = '기타', #{otherContent}, #{RREP_CONTENT})
		, date_format(now(), '%Y%m%d%H%i%s')
		, '0'
		)

mapper.update:
update 	OWNER
set		OWN_NAME	= #{OWN_NAME}
		, OWN_TEL 	= #{OWN_TEL}
		, OWN_EMAIL = #{OWN_EMAIL}
		, OWN_REDATE= DATE_FORMAT(SYSDATE(), '%Y%m%d%H%i%s')
where	OWN_NO = #{OWN_NO};
